INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (6, CAST(0x00009EC3009191A0 AS DateTime), N'Erin Leary', N'CodeRush Tip – The References Toolwindow', N'<p>I love this feature for finding references, and have been using it for a while to find references when I have the cursor over a type. But maybe I''m missing it, how can I search for a type I type in? For example, I want to find all references of "EmailUtility" but I''m in some random piece of code that doesn''t use it. Is there a way to press Ctrl-F12 and type EmailUtility for it to find all references?</p>', 0, N'IDE Tools', 1)
GO
INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (7, CAST(0x00009EC300940A70 AS DateTime), N'Rory Becker - DevExpress', N'CodeRush Tip – The References Toolwindow', N'<p>@Erin sorry Erin AFAIK this isn''t possible. </p>
<p>However there is another feature that you might try looking at called QuickNav. </p>
<p>I''ll be posting on this soon, but in essence it''s a Quick Search for Types Etc</p>
<p>Trigger it with Ctrl+Shift+Q and be sure to explore the drop down set of options on the right</p>', 0, N'IDE Tools', 1)
GO
