INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (81, CAST(0x00009ECC00979C80 AS DateTime), N'Adlay Almeida (AA)', N'WPF and Silverlight Gauge Controls', N'<p>I agree with Brian on this. I love the XtraGauges, but when in the process of choosing one for an app, they all tend too grab too much attention with the color selection and such. These look far simpler and cleaner.</p>', 0, N'WinForms', 1)
