INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (89, CAST(0x00009ECD00761A60 AS DateTime), N'Slava D (DevExpress)', N'XPO – 11.1 Sneak Peek – Data caching improvements', N'<p>@M. Brekhof:</p>
