INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (92, CAST(0x00009ECD00A252B0 AS DateTime), N'Slava D (DevExpress)', N'XPO – 11.1 Sneak Peek – Data caching improvements', N'<p>@Luca Poretti:</p>
