INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (93, CAST(0x00009ECD00B42D00 AS DateTime), N'Dennis (DevExpress Support)', N'XAF – Model Merge Tool (coming in v2011 vol1)', N'<p>@All: We have posted a new blog that describes how to merge a user model stored in a database: <a rel="nofollow" target="_new" href="http://community.devexpress.com/blogs/eaf/archive/2011/04/22/xaf-merge-user-differences-from-a-custom-model-storage.aspx">community.devexpress.com/.../xaf-merge-user-differences-from-a-custom-model-storage.aspx</a></p>', 0, N'Frameworks', 1)
