INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (175, CAST(0x00009EF7008B86C0 AS DateTime), N'Jugo Radonic', N'11.1 Feature Days (from TechEd) – New ASP.NET MVC Reporting Extensions', N'<p>Great stuff! </p>
