INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (176, CAST(0x00009EF700CCE520 AS DateTime), N'Mark Miller (DevExpress)', N'New CodeRush Feature: Quick Pair (Parens, Brackets, Quotes, etc.) for Visual Studio', N'<p>Bob Archer: It''s certainly possible to add a missing leading paren when the closing paren is pressed. Maybe we''ll write that in a future CodeRush Feature Workshop (same for your suggestion about automatically adding parens when a method is selected in Intellisense). &nbsp;</p>', 0, N'IDE Tools', 1)
