INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (194, CAST(0x00009F0300888150 AS DateTime), N'Alexey X', N'ASP.NET MVC - How To Embed MVC GridView Extension In To Tab Strip Extension', N'<p>Hello!</p>
