INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (264, CAST(0x00009F14009B2E90 AS DateTime), N'Peter Thorpe', N'DXperience moving to .NET 3.5 or above after next major release', N'<p>Although I said earlier I do have a project that is stuck at 3.5. After thinking about it your first release not supporting 2.0 is a year down the line from now, plenty of time for me and others to sort these issues. Even if I can''t fix it I will just leave it with the last version of the controls supporting that version. 4.0 FTW. </p>
<p>You could always poll it like you did with IE 6.</p>
', 0, N'Announcements', 1)
GO
INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (265, CAST(0x00009F1400A55820 AS DateTime), N'Roger Parry', N'DXperience moving to .NET 3.5 or above after next major release', N'<p>3.5 is fine for me (probably 4 would be OK too)</p>
', 0, N'Announcements', 1)
