INSERT [dbo].[Messages] ([ID], [Date], [From], [Subject], [Text], [HasAttachment], [Folder], [IsReply]) VALUES (272, CAST(0x00009F15015B2B00 AS DateTime), N'Kurt Wehrend', N'DXperience moving to .NET 3.5 or above after next major release', N'<p>I think DevExpress needs to take the leap and move straight to 4.0. Our company platform is now supporting 4.0 plus as you said WPF/Silverlight requires it and are gaining more traction in the technology space.</p>
